import React, { Component } from 'react';
import '../App.css';
import axios from 'axios';
import { Link } from 'react-router-dom';
import SongCard from './SongCard';

class ShowSongList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      songs: []
    };
  }

  componentDidMount() {
    axios
      .get('http://localhost:8082/api/songs')
      .then(res => {
        this.setState({
          songs: res.data
        })
      })
      .catch(err =>{
        console.log('Error from ShowSongList');
      })
  };


  render() {
    const songs = this.state.songs;
    console.log("PrintSong: " + songs);
    let songList;

    if(!songs) {
      songList = "there is no song record!";
    } else {
      songList = songs.map((song, k) =>
        <SongCard song={song} key={k} />
      );
    }

    return (
      <div className="ShowSongList">
        <div className="container">
          <div className="row">
            <div className="col-md-12">
              <br />
              <h2 className="display-4 text-center">Songs List</h2>
            </div>

            <div className="col-md-11">
              <Link to="/create-song" className="btn btn-outline-warning float-right">
                + Add New Song
              </Link>
              <br />
              <br />
              <hr />
            </div>

          </div>

          <div className="list">
                {songList}
          </div>
        </div>
      </div>
    );
  }
}
export default ShowSongList;
